import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
function Success() {
  const { id } = useParams();
  const [paymentInfo, setPaymentInfo] = useState(null);

  useEffect(async () => {
    try {
      const res = await axios.get(`/api/success/${id}`);
      console.log(res);
    } catch (error) {}
  }, []);

  return (
    <>
      {paymentInfo ? (
        <div>
          <h1>Payment Successful</h1>
          <p>Session ID: {paymentInfo.id}</p>
          <p>Amount Total: {paymentInfo.amount_total}</p>
          <p>
            Created: {new Date(paymentInfo.created * 1000).toLocaleString()}
          </p>
          <p>Status: {paymentInfo.status}</p>
          {/* Display other success information as needed */}
        </div>
      ) : (
        <p>Loading payment information...</p>
      )}
    </>
  );
}

export default Success;
