import React, { useEffect } from "react";
import { useLocation } from "react-router-dom";

const Success = () => {
  const location = useLocation();

  useEffect(() => {
    const sessionId = new URLSearchParams(location.search).get("session_id");

    // Fetch the Checkout Session to get metadata
    // Replace 'YOUR_STRIPE_SECRET_KEY' with your actual secret key
    fetch(`/api/get-session?sessionId=${sessionId}`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        Authorization: `Bearer YOUR_STRIPE_SECRET_KEY`,
      },
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Order details:", data.metadata);
        // Process the order details as needed
      })
      .catch((error) => {
        console.error("Error fetching Checkout Session:", error);
      });
  }, [location.search]);

  return (
    <div>
      <h1>Payment Successful</h1>
      {/* Display other success page content */}
    </div>
  );
};

export default Success;
