import React, { useContext, useEffect, useState } from "react";
import { GlobalState } from "../../../GlobalState";
import axios from "axios";
import { loadStripe } from "@stripe/stripe-js";

import { formatCurrency } from "../utils/formatcurrency/FormatCurrency";
import { TruncatedText } from "../utils/truncated/TruncatedText"

function Cart() {
  const state = useContext(GlobalState);
  const [cart, setCart] = state.userAPI.cart;
  const [token] = state.token;
  const [total, setTotal] = useState(0);

  const stripePromise = loadStripe(
    process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY
  );

  const addToCart = async (updatedCart) => {
    await axios.patch(
      "/user/addcart",
      { cart: updatedCart },
      {
        headers: { Authorization: token },
      }
    );
  };

  const handleCheckout = async () => {
    const lineItems = cart.map((item) => ({
      price_data: {
        currency: "vnd",
        product_data: {
          name: item.title,
          images: [item.images.url],
          metadata: {
            id: item.id,
          },
        },
        unit_amount: item.price,
      },
      quantity: item.quantity,
    }));

    try {
      // Create a checkout session
      const { data } = await axios.post(
        "/api/payment",
        { lineItems },
        {
          headers: { Authorization: token },
        }
      );

      // Initialize Stripe
      const stripe = await stripePromise;

      // Redirect to Checkout
      await stripe.redirectToCheckout({ sessionId: data.id });

      // setCart([])
    } catch (error) {
      console.error("Error during checkout:", error);
    }
  };

  useEffect(() => {
    const getTotal = () => {
      const total = cart.reduce(
        (prev, item) => prev + item.price * item.quantity,
        0
      );
      setTotal(total);
    };
    getTotal();
  }, [cart]);

  const increment = (id) => {
    const updatedCart = cart.map((item) => ({
      ...item,
      quantity: item._id === id ? item.quantity + 1 : item.quantity,
    }));
    setCart(updatedCart);
    addToCart(updatedCart);
  };

  const decrement = (id) => {
    const updatedCart = cart.filter((item) => item._id !== id);
    setCart(updatedCart);
    addToCart(updatedCart);
  };

  const removeProduct = (id) => {
    if (window.confirm("Bạn có chắc muốn xóa sản phẩm?")) {
      const updatedCart = cart.filter((item) => item._id !== id);
      setCart(updatedCart);
      addToCart(updatedCart);
    }
  };

  if (cart.length === 0)
    return (
      <h2 style={{ textAlign: "center", fontSize: "5rem" }}>Giỏ hàng trống</h2>
    );
  return (
    <div>
      {cart.map((product) => (
        <div className="flex border-2 my-4 rounded-lg" key={product._id}>
          <img className="max-w-[30%] max-h-52" src={product.images?.url} alt="" />
          <div className="box-detail flex-1">
            <div className="row">
              <h2>{product.title}</h2>
            </div>
            <span className="detail-price">
              {formatCurrency(product.price * product.quantity)}
            </span>
            <TruncatedText text={product.description} limit={20} />
            <p>{product.content}</p>
            <div className="amount">
              <button onClick={() => decrement(product._id)}> - </button>
              <span>{product.quantity}</span>
              <button onClick={() => increment(product._id)}> + </button>
            </div>
            <div className="delete" onClick={() => removeProduct(product._id)}>
              X
            </div>
          </div>
        </div>
      ))}
      <div className="total">
        <h3>
          Tổng cộng: <strong>{formatCurrency(total)}</strong>
        </h3>
        <button className="px-6 p-y2 bg-blue-600 text-white text-xl" onClick={handleCheckout}>Thanh toán</button>
      </div>
    </div>
  );
}

export default Cart;
