import axios from "axios";
import React, { useEffect, useState } from "react";
import { useParams,Link } from "react-router-dom";
function Success() {
  const { id } = useParams();
  const [paymentInfo, setPaymentInfo] = useState(null);

  useEffect(async () => {
    try {
      const res = await axios.get(`/api/success/${id}`);
      setPaymentInfo(res.data);
    } catch (error) {}
  }, []);

  return (
    <>
      {paymentInfo ? (
        <div className="success-page">
          <h1>Thanh toán thành công</h1>
          <p>Số tiền: <span>{paymentInfo.amount_total}</span></p>
          <p>
            Ngày thành toán: <span>{new Date(paymentInfo.created * 1000).toLocaleString()}</span>
          </p>
          <p>Trạng thái: {paymentInfo.status =="complete" ? "Đã thanh toán" : "Chưa thanh toán"}</p>
          <Link to='/'>Tiếp tục mua sắm</Link>
          <Link to='/history'>Xem đơn hàng</Link>
        </div>
      ) : (
        <p>Đang tải thông tin...</p>
      )}
    </>
  );
}

export default Success;
