import React, { useContext, useEffect, useState } from "react";
import { GlobalState } from "../../../GlobalState";
// import { Link } from "react-router-dom";
import axios from "axios";
import { loadStripe } from "@stripe/stripe-js";

function Cart() {
  const state = useContext(GlobalState);
  const [cart, setCart] = state.userAPI.cart;
  const [token] = state.token;
  const [total, setTotal] = useState(0);

  const stripePromise = loadStripe(
    process.env.REACT_APP_STRIPE_PUBLISHABLE_KEY
  );

  const handleCheckout = async () => {
    const lineItems = cart.map((item) => {
      return {
        price_data: {
          currency: "vnd",
          product_data: {
            name: item.title,
            images: [item.images.url],
            metadata: {
              id: item.id,
            },
          },
          unit_amount: item.price,
        },
        quantity: item.quantity,
      };
    });
    try {
      // Create a checkout session
      const { data } = await axios.post("/api/payment", {
        lineItems,
      });

      // Initialize Stripe
      const stripe = await stripePromise;

      // Redirect to Checkout
      const result = await stripe.redirectToCheckout({ sessionId: data.id });

      // Check if the redirect was successful
      if (result.error) {
        console.error(result.error.message);
      } else {
        // If the redirect was successful, clear the cart and update the user's cart in the database
        setCart([]);
        addToCart([]);
      }
    } catch (error) {
      console.error("Error during checkout:", error);
    }
  };

  useEffect(() => {
    const getTotal = () => {
      const total = cart.reduce((prev, item) => {
        return prev + item.price * item.quantity;
      }, 0);
      setTotal(total);
    };
    getTotal();
  }, [cart]);

  const addToCart = async (cart) => {
    await axios.patch(
      "/user/addcart",
      { cart },
      {
        headers: { Authorization: token },
      }
    );
  };

  const increment = (id) => {
    cart.forEach((item) => {
      if (item._id === id) {
        item.quantity += 1;
      }
    });
    setCart([...cart]);
    addToCart(cart);
  };

  const decrement = (id) => {
    cart.forEach((item) => {
      if (item._id === id) {
        item.quantity === 1 ? (item.quantity = 1) : (item.quantity -= 1);
      }
    });
    setCart([...cart]);
    addToCart(cart);
  };

  const removeProduct = (id) => {
    if (window.confirm("Bạn có chắc muốn xóa sản phẩm?")) {
      cart.forEach((item, index) => {
        if (item._id === id) {
          cart.splice(index, 1);
        }
      });
      setCart([...cart]);
      addToCart(cart);
    }
  };

  if (cart.length === 0)
    return (
      <h2 style={{ textAlign: "center", fontSize: "5rem" }}>Giỏ hàng trống</h2>
    );
  return (
    <div>
      {cart.map((product) => (
        <div className="detail cart" key={product._id}>
          <img src={product.images.url} alt="" />
          <div className="box-detail">
            <div className="row">
              <h2>{product.title}</h2>
              <h6>#id: {product.product_id}</h6>
            </div>
            <span className="detail-price">
              {product.price * product.quantity} VND
            </span>
            <p>{product.description}</p>
            <p>{product.content}</p>
            <div className="amount">
              <button onClick={() => decrement(product._id)}> - </button>
              <span>{product.quantity}</span>
              <button onClick={() => increment(product._id)}> + </button>
            </div>
            <div className="delete" onClick={() => removeProduct(product._id)}>
              X
            </div>
          </div>
        </div>
      ))}
      <div className="total">
        <h3>
          Tổng cộng: <strong>{total} VND</strong>
        </h3>
        <button onClick={handleCheckout}>Checkout</button>
      </div>
    </div>
  );
}

export default Cart;
